#include <iostream>
#include <fstream>
#include "alg_graphs.h"
#include "alg_strings.h"
#include "alg_stopwatch.h"

using namespace std;

std::string ReadFile(const std::string &filename)
{
  std::ifstream file(filename);
  if (!file)
  {
    cerr << "Unable to open file " << filename << endl;
    return "";
  }

  std::string content((std::istreambuf_iterator<char>(file)),
                      std::istreambuf_iterator<char>());
  file.close();
  return content;
}

void RunKMP(std::string pattern, std::string text)
{
  KMP kmp(pattern);
  StopWatch sw;
  int index = kmp.search(text);
  double time_taken = sw.elapsed_time_milli_seconds();
  if (index != text.length())
  {
    cout << "Pattern found at index: " << index << endl;
  }
  else
  {
    cout << "Pattern not found." << endl;
  }
  cout << "KMP search time: " << time_taken << " ms." << endl;
}

void RunRabinKarp(std::string pattern, std::string text)
{
  RabinKarp rk(pattern);
  StopWatch sw;
  int pos = rk.search(text);
  double time_taken = sw.elapsed_time_milli_seconds();
  if (pos != text.length())
  {
    cout << "Rabin-Karp: Pattern found at index: " << pos << endl;
  }
  else
  {
    cout << "Rabin-Karp: Pattern not found." << endl;
  }
  cout << "Rabin-Karp search time: " << time_taken << " ms." << endl;
}

/******************************************************************************
 *  Main program to compare Rabin-Karp and KMP string-matching algorithms.
 ******************************************************************************/
int main()
{
  std::string pattern = "";
  std::string text = ReadFile("../resources/String100000.txt");

  RunKMP(pattern, text);
  RunRabinKarp(pattern, text);
  return 0;
}
